mapper.select:
SELECT board_no, board_title, board_content, board_writer, board_reg_date, board_mod_date, board_view, c.category_content, (SELECT EXISTS (SELECT 1 FROM file WHERE board_no = b.board_no)) AS is_exist_file
FROM board AS b JOIN category c ON c.category_no = b.category_no
WHERE b.board_no = #{boardNo}

mapper.update:
UPDATE board

SET board_view = board_view + 1

WHERE board_no = #{boardNo}

UPDATE board
SET board_writer = #{boardWriter}, board_title = #{boardTitle}, board_content = #{boardContent}, board_mod_date = current_timestamp
WHERE board_no = #{boardNo}

mapper.select:
SELECT board_password

FROM board

WHERE board_no = #{boardNo}

SELECT board_no

FROM board

WHERE board_no = #{boardNo}
